import tictactoe.Main;

import java.util.ArrayList;
import java.util.List;
import java.util.stream.Collectors;


class Attach {
    String inputField;
    String outField;

    Attach(String inputField, String outField) {
        this.inputField = inputField;
        this.outField = outField;
    }
}


