public class TicTacToeTest extends MainMethodTest<Attach> {
    public TicTacToeTest() throws Exception {
        super(Main.class);
    }

    @Override
    public List<TestCase<Attach>> generateTestCases() {
        return List.of(
            new TestCase<Attach>()
                .setInput("\"X X O    \"\n1 2")
                .setAttach(new Attach(
                    "\"X X O    \"",
                     "\"X XXO    \"")),

            new TestCase<Attach>()
                .setInput("\"X X O    \"\n1 1")
                .setAttach(new Attach(
                    "\"X X O    \"",
                     "\"X X O X  \"")),

            new TestCase<Attach>()
                .setInput("\"X X O    \"\n3 2")
                .setAttach(new Attach(
                    "\"X X O    \"",
                     "\"X X OX   \"")),

            new TestCase<Attach>()
                .setInput("\"X X O    \"\n2 1")
                .setAttach(new Attach(
                    "\"X X O    \"",
                     "\"X X O  X \"")),

            new TestCase<Attach>()
                .setInput("\" XXOO OX \"\n1 3")
                .setAttach(new Attach(
                    "\" XXOO OX \"",
                     "\"XXXOO OX \"")),

            new TestCase<Attach>()
                .setInput("\" XXOO OX \"\n3 1")
                .setAttach(new Attach(
                    "\" XXOO OX \"",
                     "\" XXOO OXX\"")),

            new TestCase<Attach>()
                .setInput("\" XXOO OX \"\n3 2")
                .setAttach(new Attach(
                    "\" XXOO OX \"",
                     "\" XXOOXOX \""))

        );
    }

    @Override
    public CheckResult check(String reply, Attach clue) {

        List<TicTacToeField> fields = TicTacToeField.parseAll(reply);

        if (fields.size() != 2) {
            return new CheckResult(false,
                "You should output exactly 2 fields, found: " + fields.size());
        }

        TicTacToeField curr = fields.get(0);
        TicTacToeField next = fields.get(1);

        TicTacToeField correctCurr = new TicTacToeField(clue.inputField);
        TicTacToeField correctNext = new TicTacToeField(clue.outField);

        if (!curr.equalTo(correctCurr)) {
            return new CheckResult(false,
                "The first field is not equal to the input field");
        }

        if (!next.equalTo(correctNext)) {
            return new CheckResult(false,
                "The first field is correct, but the second is not");
        }

        return CheckResult.TRUE;
    }
}

