        }
        return improved;
    }

    boolean isCloseTo(TicTacToeField other) {
        return equalTo(other)
            || hasNextAs(other)
            || other.hasNextAs(this);
    }

    static TicTacToeField parse(String fieldStr) {

        List<String> lines = fieldStr
            .lines()
            .map(String::strip)
            .filter(e ->
                e.startsWith("|")
                    && e.endsWith("|"))
            .collect(Collectors.toList());

        for (String line : lines) {
            for (char c : line.toCharArray()) {
                if (c != 'X'
                    && c != 'O'
                    && c != '|'
                    && c != ' ') {
                    return null;
                }
            }
        }

        FieldState[][] field = new FieldState[3][3];

        int y = 2;
        for (String line : lines) {
            char[] cols = new char[] {
                line.charAt(2),
                line.charAt(4),
                line.charAt(6)
            };

            int x = 0;
            for (char c : cols) {
                FieldState state = FieldState.get(c);
                if (state == null) {
                    return null;
                }
                field[y][x] = state;
                x++;
            }
            y--;
        }

        return new TicTacToeField(field);
    }


    static List<TicTacToeField> parseAll(String output) {
        List<TicTacToeField> fields = new ArrayList<>();

        List<String> lines = output
            .lines()
            .map(String::strip)
            .filter(e -> e.length() > 0)
            .collect(Collectors.toList());

        String candidateField = "";
        boolean insideField = false;
        for (String line : lines) {
            if (line.contains("----") && !insideField) {
                insideField = true;
                candidateField = "";
            } else if (line.contains("----") && insideField) {
                TicTacToeField field = TicTacToeField.parse(candidateField);
                if (field != null) {
                    fields.add(field);
                }
                insideField = false;
            }

            if (insideField && line.startsWith("|")) {
                candidateField += line + "\n";
            }
        }

        return fields;
    }

}


class Clue {
    int x, y;
    String input;
    Clue(String input, int x, int y) {
        this.input = input;
        this.x = x;
        this.y = y;
    }
}

public class TicTacToeTest extends MainMethodTest<Clue> {
    public TicTacToeTest() throws Exception {
        super(Main.class);
    }

    // TODO: why if this field in not static then it is null, not String[]?
    static final String[] inputs = new String[] {
        "1 1", "1 2", "1 3",
        "2 1", "2 2", "2 3",
        "3 1", "3 2", "3 3"
    };

    String iterateCells(String initial) {
        int index = -1;
        for (int i = 0; i < inputs.length; i++) {
            if (initial.equals(inputs[i])) {
                index = i;
                break;
            }
        }
        if (index == -1) {
            return "";
        }
        String fullInput = "";
        for (int i = index; i < index + 9; i++) {
            fullInput += inputs[i % inputs.length] + "\n";
        }
        return fullInput;
    }

    @Override
    public List<TestCase<Clue>> generateTestCases() {

        List<TestCase<Clue>> tests = new ArrayList<>();

        int i = 0;

        for (String startField : new String[] {
            "\" XXOO OX \"",
            "\"         \"",
            "\"X X O    \""
        }) {

            for (String input : inputs) {
                String fullInput = iterateCells(input);

                String[] strNums = input.split("\\s+");
                int x = Integer.parseInt(strNums[0]);
                int y = Integer.parseInt(strNums[1]);

                if (i % 2 == 1) {
                    // mix with incorrect data
                    fullInput = "4 " + i + "\n" + fullInput;
                }

                tests.add(new TestCase<Clue>()
                    .setInput(startField + "\n" + fullInput)
                    .setAttach(new Clue(startField, x, y)));

                i++;
            }

        }

        return tests;
    }

    @Override
    public CheckResult check(String reply, Clue clue) {

        List<TicTacToeField> fields = TicTacToeField.parseAll(reply);

        if (fields.size() != 2) {
            return new CheckResult(false,
                "Can't find two fields inside output");
        }

        TicTacToeField curr = fields.get(0);
        TicTacToeField next = fields.get(1);

        TicTacToeField correctCurr = new TicTacToeField(clue.input);
        TicTacToeField correctNext = new TicTacToeField(correctCurr.field);

        String[] numInputs = iterateCells(clue.x + " " + clue.y).split("\n");
        for (String input : numInputs) {
            String[] strNums = input.split(" ");
            int x = Integer.parseInt(strNums[0]);
            int y = Integer.parseInt(strNums[1]);
            if (correctNext.field[y - 1][x - 1] == FieldState.FREE) {
                correctNext.field[y - 1][x - 1] = FieldState.X;
                break;
            }
        }

        if (!curr.equalTo(correctCurr)) {
            return new CheckResult(false,
                "The first field is not equal to the input field");
        }

        if (!next.equalTo(correctNext)) {
            return new CheckResult(false,
                "The first field is correct, but the second is not");
        }

        return CheckResult.TRUE;
    }
}

package tictactoe;

public class Main {
    public static void main(String[] args) {
        System.out.println("Hello World!");
    }
}

package tictactoe;

import java.util.HashMap;
import java.util.Map;

class Status {
    static String drawField(char[][] str) {
        StringBuilder stringBuilder = new StringBuilder();
        stringBuilder.append("---------\n");
        for (int i = 0; i < 3; i++) {
            stringBuilder.append("| ");
            for (int j = 0; j < 3; j++) {
                stringBuilder.append(str[i][j]);
                stringBuilder.append(" ");
            }
            stringBuilder.append("|\n");
        }
        stringBuilder.append("---------");
        return stringBuilder.toString();
    }

    static char[][] toMatrix(String str) {
        char[][] ans = new char[3][3];
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                char ch = str.charAt(3*i+j);
                ans[i][j] = ch;
            }
        }
        return ans;
    }

    static String status(char[][] matrix) {
        Map<Character, Integer> num = new HashMap<>();
        /*
        Check if drawField has a lot more X's that O's or vice versa.
         */
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                int count = num.getOrDefault(matrix[i][j], 0);
                num.put(matrix[i][j], count + 1);
            }
        }
        if (Math.abs(num.get('X') - num.get('O')) > 1) {
            return "Impossible";
        }

        Map<Character, Boolean> winner = new HashMap<>();
        winner.put('X', false);
        winner.put('O', false);
        /*
        Check who has 3 i a row.
         */
        for (int i = 0; i < 3; i++) {
            char first = matrix[i][0];
            if (matrix[i][1] == first && matrix[i][2] == first) {
                winner.put(first, true);
            }
        }
        for (int j = 0; j < 3; j++) {
            char first = matrix[0][j];
            if (matrix[1][j] == first && matrix[2][j] == first) {
                winner.put(first, true);
            }
        }
        {
            char first = matrix[0][0];
            if (matrix[1][1] == first && matrix[2][2] == first) {
                winner.put(first, true);
            }
        }
        {
            char first = matrix[0][2];
            if (matrix[1][1] == first && matrix[2][0] == first) {
                winner.put(first, true);
            }
        }

        if (winner.get('X') && winner.get('O')) {
            return "Impossible";
        }
        if (winner.get('X')) {
            return "X wins";
        }
        if (winner.get('O')) {
            return "O wins";
        }
        if (num.getOrDefault(' ', 0).equals(0)) {
            return "Draw";
        }
        return "Game not finished";
    }
}

package tictactoe;

public class Main {
    public static void main(String[] args) {
        System.out.println("Hello World!");
    }
}

import org.hyperskill.hstest.v4.stage.MainMethodTest;
import org.hyperskill.hstest.v4.testcase.CheckResult;
import org.hyperskill.hstest.v4.testcase.TestCase;
import tictactoe.Main;

import java.util.ArrayList;
import java.util.List;
import java.util.stream.Collectors;

enum FieldState {
    X, O, FREE;

    static FieldState get(char symbol) {
        switch (symbol) {
            case 'X': return X;
            case 'O': return O;
            case ' ': return FREE;
            default: return null;
        }
    }
}

class TicTacToeField {

    final FieldState[][] field;

    TicTacToeField(FieldState[][] field) {
        this.field = new FieldState[3][3];
        for (int row = 0; row < 3; row++) {
            for (int col = 0; col < 3; col++) {
                this.field[row][col] = field[row][col];
            }
        }
    }

    TicTacToeField(String str) {
        field = new FieldState[3][3];
        str = str.replace("\"", "");

        for (int row = 0; row < 3; row++) {
            for (int col = 0; col < 3; col++) {
                field[row][col] =
                    FieldState.get(str.charAt(((2 - row) * 3 + col)));
            }
        }
    }

    boolean equalTo(TicTacToeField other) {
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                if (field[i][j] != other.field[i][j]) {
                    return false;
                }
            }
        }
        return true;
    }

    boolean hasNextAs(TicTacToeField other) {
        boolean improved = false;
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                if (field[i][j] != other.field[i][j]) {
                    if (field[i][j] == FieldState.FREE && !improved) {
                        improved = true;
                    }
                    else {
                        return false;
                    }
                }
            }
        }
        return improved;
    }

    boolean isCloseTo(TicTacToeField other) {
        return equalTo(other)
            || hasNextAs(other)
            || other.hasNextAs(this);
    }

    static TicTacToeField parse(String fieldStr) {

        List<String> lines = fieldStr
            .lines()
            .map(String::strip)
            .filter(e ->
                e.startsWith("|")
                    && e.endsWith("|"))
            .collect(Collectors.toList());

        for (String line : lines) {
            for (char c : line.toCharArray()) {
                if (c != 'X'
                    && c != 'O'
                    && c != '|'
                    && c != ' ') {
                    return null;
                }
            }
        }

        FieldState[][] field = new FieldState[3][3];

        int y = 2;
        for (String line : lines) {
            char[] cols = new char[] {
                line.charAt(2),
                line.charAt(4),
                line.charAt(6)
            };

            int x = 0;
            for (char c : cols) {
                FieldState state = FieldState.get(c);
                if (state == null) {
                    return null;
                }
                field[y][x] = state;
                x++;
            }
            y--;
        }

        return new TicTacToeField(field);
    }


    static List<TicTacToeField> parseAll(String output) {
        List<TicTacToeField> fields = new ArrayList<>();

        List<String> lines = output
            .lines()
            .map(String::strip)
            .filter(e -> e.length() > 0)
            .collect(Collectors.toList());

        String candidateField = "";
        boolean insideField = false;
        for (String line : lines) {
            if (line.contains("----") && !insideField) {
                insideField = true;
                candidateField = "";
            } else if (line.contains("----") && insideField) {
                TicTacToeField field = TicTacToeField.parse(candidateField);
                if (field != null) {
                    fields.add(field);
                }
                insideField = false;
            }

            if (insideField && line.startsWith("|")) {
                candidateField += line + "\n";
            }
        }

        return fields;
    }

}


class Clue {
    String input;
    Clue(String input) {
        this.input = input;
    }
}

public class TicTacToeTest extends MainMethodTest<Clue> {
    public TicTacToeTest() throws Exception {
        super(Main.class);
    }

    static String[] inputs = new String[] {
        "1 1", "1 2", "1 3",
        "2 1", "2 2", "2 3",
        "3 1", "3 2", "3 3"
    };

    String iterateCells(String initial) {
        int index = -1;
        for (int i = 0; i < inputs.length; i++) {
            if (initial.equals(inputs[i])) {
                index = i;
                break;
            }
        }
        if (index == -1) {
            return "";
        }
        String fullInput = "";
        for (int i = index; i < index + 9; i++) {
            fullInput += inputs[i % inputs.length] + "\n";
        }
        return fullInput;
    }

    @Override
    public List<TestCase<Clue>> generateTestCases() {
        return List.of(
            new TestCase<Clue>()
                .setInput("\" XXOO OX \"")
                .setAttach(new Clue(
                    "\" XXOO OX \"")),

            new TestCase<Clue>()
                .setInput("\"         \"")
                .setAttach(new Clue(
                    "\"         \"")),

            new TestCase<Clue>()
                .setInput("\"X X O    \"")
                .setAttach(new Clue(
                    "\"X X O    \""))
        );
    }

    @Override
    public CheckResult check(String reply, Clue clue) {

        List<TicTacToeField> fields = TicTacToeField.parseAll(reply);

        if (fields.size() != 2) {
            return new CheckResult(false,
                "Output should contain 2 fields, found: " + fields.size());
        }

        if (!reply.contains("Making move level \"easy\"")) {
            return new CheckResult(false,
                "No \"Making move level \"easy\"\" line in output");
        }

        TicTacToeField curr = fields.get(0);
        TicTacToeField next = fields.get(1);

        TicTacToeField correctCurr = new TicTacToeField(clue.input);

        if (!curr.equalTo(correctCurr)) {
            return new CheckResult(false,
                "The first field is not equal to the input field");
        }

        if (curr.equalTo(next)) {
            return new CheckResult(false,
                "The first field is equals to next, but should be different");
        }

        if (!curr.hasNextAs(next)) {
            return new CheckResult(false,
                "The first field is correct, but the second is not");
        }

        return CheckResult.TRUE;
    }
}

package tictactoe;

import java.util.HashMap;
import java.util.Map;
import java.util.NoSuchElementException;
import java.util.StringTokenizer;

class Status {
    String drawField(char[][] str) {
        StringBuilder stringBuilder = new StringBuilder();
        stringBuilder.append("---------\n");
        for (int i = 0; i < 3; i++) {
            stringBuilder.append("| ");
            for (int j = 0; j < 3; j++) {
                stringBuilder.append(str[i][j]);
                stringBuilder.append(" ");
            }
            stringBuilder.append("|\n");
        }
        stringBuilder.append("---------");
        return stringBuilder.toString();
    }

    char[][] toMatrix(String str) {
        char[][] ans = new char[3][3];
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                char ch = str.charAt(3*i+j);
                ans[i][j] = ch;
            }
        }
        return ans;
    }

    String status(char[][] matrix) {
        Map<Character, Integer> num = new HashMap<>();
        /*
        Check if drawField has a lot more X's that O's or vice versa.
         */
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                int count = num.getOrDefault(matrix[i][j], 0);
                num.put(matrix[i][j], count + 1);
            }
        }
        if (Math.abs(num.get('X') - num.get('O')) > 1) {
            return "Impossible";
        }

        Map<Character, Boolean> winner = new HashMap<>();
        winner.put('X', false);
        winner.put('O', false);
        /*
        Check who has 3 i a row.
         */
        for (int i = 0; i < 3; i++) {
            char first = matrix[i][0];
            if (matrix[i][1] == first && matrix[i][2] == first) {
                winner.put(first, true);
            }
        }
        for (int j = 0; j < 3; j++) {
            char first = matrix[0][j];
            if (matrix[1][j] == first && matrix[2][j] == first) {
                winner.put(first, true);
            }
        }
        {
            char first = matrix[0][0];
            if (matrix[1][1] == first && matrix[2][2] == first) {
                winner.put(first, true);
            }
        }
        {
            char first = matrix[0][2];
            if (matrix[1][1] == first && matrix[2][0] == first) {
                winner.put(first, true);
            }
        }

        if (winner.get('X') && winner.get('O')) {
            return "Impossible";
        }
        if (winner.get('X')) {
            return "X wins";
        }
        if (winner.get('O')) {
            return "O wins";
        }
        if (num.getOrDefault(' ', 0).equals(0)) {
            return "Draw";
        }
        return "Game not finished";
    }

    class Answer {
        private Boolean aBoolean;
        private String string;

        Answer(boolean b, String s) {
            aBoolean = b;
            string = s;
        }

        Boolean getaBoolean() {
            return aBoolean;
        }

        String getString() {
            return string;
        }
    }

    Answer parseInput(String input, char[][] field) {
        StringTokenizer stringTokenizer = new StringTokenizer(input);
        int x;
        int y;
        try {
            x = Integer.parseInt(stringTokenizer.nextToken());
            y = Integer.parseInt(stringTokenizer.nextToken());
        } catch (NumberFormatException | NoSuchElementException e) {
            return new Answer(false, "You should enter numbers!");
        }

        if (x < 1 || x > 3 || y < 1 || y > 3) {
            return new Answer(false, "Coordinates should be from 1 to 3!");
        }

        if (field[3-y][x-1] != ' ') {
            return new Answer(false, "This cell is occupied! Choose another one!");
        }

        field[3-y][x-1] = 'X';
        return new Answer(true, drawField(field));
    }
}

