class Clue {
    int x, y;
    Clue(int x, int y) {
        this.x = x;
        this.y = y;
    }
}

public class TicTacToeTest extends MainMethodTest<Clue> {
    public TicTacToeTest() throws Exception {
        super(Main.class);
    }

    static String[] inputs = new String[] {
        "1 1", "1 2", "1 3",
        "2 1", "2 2", "2 3",
        "3 1", "3 2", "3 3"
    };

    String iterateCells(String initial) {
        int index = -1;
        for (int i = 0; i < inputs.length; i++) {
            if (initial.equals(inputs[i])) {
                index = i;
                break;
            }
        }
        if (index == -1) {
            return "";
        }
        String fullInput = "";
        for (int i = index; i < index + 9; i++) {
            fullInput += inputs[i % inputs.length] + "\n";
        }
        return fullInput;
    }

    @Override
    public List<TestCase<Clue>> generateTestCases() {

        List<TestCase<Clue>> tests = new ArrayList<>();

        int i = 0;
        for (String input : inputs) {
            String fullMoveInput = iterateCells(input);

            String[] strNums = input.split(" ");
            int x = Integer.parseInt(strNums[0]);
            int y = Integer.parseInt(strNums[1]);

            if (i % 2 == 1) {
                // mix with incorrect data
                fullMoveInput = "4 " + i + "\n" + fullMoveInput;
            }

            String fullGameInput = "";
            for (int j = 0; j < 9; j++) {
                fullGameInput += fullMoveInput;
            }

            String initial;

            switch (i % 4) {
                case 0: initial = "start user easy\n"; break;
                case 1: initial = "start easy user\n"; break;
                case 2: initial = "start user medium\n"; break;
                case 3: initial = "start medium user\n"; break;
                default: continue;
            }

            fullGameInput = initial + fullGameInput + "exit";

            tests.add(new TestCase<Clue>()
                .setInput(fullGameInput));

            i++;
        }

        tests.add(new TestCase<Clue>()
            .setInput("start easy easy\nexit"));

        tests.add(new TestCase<Clue>()
            .setInput("start medium medium\nexit"));

        tests.add(new TestCase<Clue>()
            .setInput("start medium easy\nexit"));

        tests.add(new TestCase<Clue>()
            .setInput("start easy medium\nexit"));

        tests.add(new TestCase<Clue>()
            .setInput("start user user\n" +
                "1 1\n" +
                "2 2\n" +
                "1 2\n" +
                "2 1\n" +
                "1 3\n" +
                "exit"));

        return tests;
    }

    @Override
    public CheckResult check(String reply, Clue clue) {

        List<TicTacToeField> fields = TicTacToeField.parseAll(reply);

        if (fields.size() == 0) {
            return new CheckResult(false, "No fields found");
        }

        for (int i = 1; i < fields.size(); i++) {
            TicTacToeField curr = fields.get(i - 1);
            TicTacToeField next = fields.get(i);

            if (!(curr.equalTo(next) || curr.hasNextAs(next))) {
                return new CheckResult(false,
                    "For two fields following each " +
                        "other one is not a continuation " +
                        "of the other.");
            }
        }

        return CheckResult.TRUE;
    }
}

package tictactoe.difficultyLevel;

import tictactoe.Status;

import java.util.Random;

public class Easy implements Player {
    private final char LETTER;

    public Easy(char ch) {
        LETTER = ch;
    }

    @Override
    public boolean makeMove(char[][] field, Status status) throws NoMoveAvailable {
        System.out.print(statusMove());
        int numberOfMoves = 0;
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                if (field[i][j] == ' ') {
                    numberOfMoves++;
                }
            }
        }
        if (numberOfMoves == 0) {
            throw new NoMoveAvailable();
        }

        Random random = new Random();
        int move = random.nextInt(numberOfMoves);
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                if (field[i][j] == ' ') {
                    if (move == 0) {
                        field[i][j] = LETTER;
                        System.out.println(status.drawField(field));
                        return status.status(field).contains("wins");
                    }
                    move--;
                }
            }
        }
        throw new NoMoveAvailable();
    }

    @Override
    public String statusMove() {
        return "Making move level \"easy\"\n";
    }
}

package tictactoe;

import tictactoe.difficultyLevel.Player;
import tictactoe.difficultyLevel.Easy;
import tictactoe.difficultyLevel.User;


class Game {
    private Player playerX;
    private Player playerO;

    static int correctInput(String string) {
        String[] strings = string.split(" ");
        if (strings.length < 1) {
            return -1;
        }
        if (strings[0].equals("exit")) {
            return 0;
        }
        if (strings[0].equals("start")) {
            if (strings.length < 3) {
                return -1;
            } else {
                return Player.isLevel(strings[1]) && Player.isLevel(strings[2]) ? 1 : -1;
            }
        }
        return -1;
    }

    Game(String X, String O) {
        switch (X) {
            case "user": playerX = new User('X'); break;
            case "easy": playerX = new Easy('X'); break;
        }

        switch (O) {
            case "user": playerO = new User('O'); break;
            case "easy": playerO = new Easy('O'); break;
        }
    }

    void processGame() {
        Status status = new Status();
        char[][] field = status.toMatrix("         ");
        System.out.println(status.drawField(field));
        while (true) {
            try {
                if (playerX.makeMove(field, status)) {
                    break;
                }
            } catch (Player.NoMoveAvailable noMoveAvailable) {
                noMoveAvailable.printStackTrace();
            }
            try {
                if (playerO.makeMove(field, status)) {
                    break;
                }
            } catch (Player.NoMoveAvailable noMoveAvailable) {
                noMoveAvailable.printStackTrace();
            }
        }
        System.out.println(status.status(field));
    }
}

package tictactoe;

import java.util.HashMap;
import java.util.Map;
import java.util.NoSuchElementException;
import java.util.StringTokenizer;

public class Status {
    public String drawField(char[][] str) {
        StringBuilder stringBuilder = new StringBuilder();
        stringBuilder.append("---------\n");
        for (int i = 0; i < 3; i++) {
            stringBuilder.append("| ");
            for (int j = 0; j < 3; j++) {
                stringBuilder.append(str[i][j]);
                stringBuilder.append(" ");
            }
            stringBuilder.append("|\n");
        }
        stringBuilder.append("---------");
        return stringBuilder.toString();
    }

    public char[][] toMatrix(String str) {
        char[][] ans = new char[3][3];
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                char ch = str.charAt(3*i+j);
                ans[i][j] = ch;
            }
        }
        return ans;
    }

    public String status(char[][] matrix) {
        Map<Character, Integer> num = new HashMap<>();
        /*
        Check if drawField has a lot more X's that O's or vice versa.
         */
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                int count = num.getOrDefault(matrix[i][j], 0);
                num.put(matrix[i][j], count + 1);
            }
        }
        if (Math.abs(num.getOrDefault('X', 0) - num.getOrDefault('O', 0)) > 1) {
            return "Impossible";
        }

        Map<Character, Boolean> winner = new HashMap<>();
        winner.put('X', false);
        winner.put('O', false);
        /*
        Check who has 3 i a row.
         */
        for (int i = 0; i < 3; i++) {
            char first = matrix[i][0];
            if (matrix[i][1] == first && matrix[i][2] == first) {
                winner.put(first, true);
            }
        }
        for (int j = 0; j < 3; j++) {
            char first = matrix[0][j];
            if (matrix[1][j] == first && matrix[2][j] == first) {
                winner.put(first, true);
            }
        }
        {
            char first = matrix[0][0];
            if (matrix[1][1] == first && matrix[2][2] == first) {
                winner.put(first, true);
            }
        }
        {
            char first = matrix[0][2];
            if (matrix[1][1] == first && matrix[2][0] == first) {
                winner.put(first, true);
            }
        }

        if (winner.get('X') && winner.get('O')) {
            return "Impossible";
        }
        if (winner.get('X')) {
            return "X wins";
        }
        if (winner.get('O')) {
            return "O wins";
        }
        if (num.getOrDefault(' ', 0).equals(0)) {
            return "Draw";
        }
        return "Game not finished";
    }

    public class Answer {
        private Boolean aBoolean;
        private String string;

        Answer(boolean b, String s) {
            aBoolean = b;
            string = s;
        }

        public Boolean getaBoolean() {
            return aBoolean;
        }

        public String getString() {
            return string;
        }
    }

    public Answer parseInput(String input, char[][] field, char ch) {
        StringTokenizer stringTokenizer = new StringTokenizer(input);
        int x;
        int y;
        try {
            x = Integer.parseInt(stringTokenizer.nextToken());
            y = Integer.parseInt(stringTokenizer.nextToken());
        } catch (NumberFormatException | NoSuchElementException e) {
            return new Answer(false, "You should enter numbers!");
        }

        if (x < 1 || x > 3 || y < 1 || y > 3) {
            return new Answer(false, "Coordinates should be from 1 to 3!");
        }

        if (field[3-y][x-1] != ' ') {
            return new Answer(false, "This cell is occupied! Choose another one!");
        }

        field[3-y][x-1] = ch;
        return new Answer(true, drawField(field));
    }
}

package tictactoe.difficultyLevel;

import tictactoe.Status;

import java.util.Scanner;

public class User implements Player {
    private final char LETTER;

    public User(char ch) {
        LETTER = ch;
    }

    @Override
    public boolean makeMove(char[][] field, Status status) {
        System.out.print(statusMove());
        Scanner scanner = new Scanner(System.in);
        String input = scanner.nextLine();
        Status.Answer answer = status.parseInput(input, field, LETTER);
        while (!answer.getaBoolean()) {
            System.out.println(answer.getString());
            input = scanner.nextLine();
            answer = status.parseInput(input, field, LETTER);
        }
        System.out.println(answer.getString());
        String gameStatus = status.status(field);
        return gameStatus.contains("wins");
    }

    @Override
    public String statusMove() {
        return "Enter the coordinates: ";
    }
}

package tictactoe;

public class Main {
    public static void main(String[] args) {
        System.out.println("Hello World!");
    }
}

import org.hyperskill.hstest.v4.stage.MainMethodTest;
import org.hyperskill.hstest.v4.testcase.CheckResult;
import org.hyperskill.hstest.v4.testcase.TestCase;
import tictactoe.Main;

import java.util.ArrayList;
import java.util.List;
import java.util.stream.Collectors;

enum FieldState {
    X, O, FREE;

    static FieldState get(char symbol) {
        switch (symbol) {
            case 'X': return X;
            case 'O': return O;
            case ' ': return FREE;
            default: return null;
        }
    }
}

class TicTacToeField {

    final FieldState[][] field;

    TicTacToeField(FieldState[][] field) {
        this.field = new FieldState[3][3];
        for (int row = 0; row < 3; row++) {
            for (int col = 0; col < 3; col++) {
                this.field[row][col] = field[row][col];
            }
        }
    }

    TicTacToeField(String str) {
        field = new FieldState[3][3];

        for (int row = 0; row < 3; row++) {
            for (int col = 0; col < 3; col++) {
                field[row][col] =
                    FieldState.get(str.charAt(((2 - row) * 3 + col)));
            }
        }
    }

    boolean equalTo(TicTacToeField other) {
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                if (field[i][j] != other.field[i][j]) {
                    return false;
                }
            }
        }
        return true;
    }

    boolean hasNextAs(TicTacToeField other) {
        boolean improved = false;
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                if (field[i][j] != other.field[i][j]) {
                    if (field[i][j] == FieldState.FREE && !improved) {
                        improved = true;
                    }
                    else {
                        return false;
                    }
                }
            }
        }
        return improved;
    }

    boolean isCloseTo(TicTacToeField other) {
        return equalTo(other)
            || hasNextAs(other)
            || other.hasNextAs(this);
    }

    static TicTacToeField parse(String fieldStr) {

        List<String> lines = fieldStr
            .lines()
            .map(String::strip)
            .filter(e ->
                e.startsWith("|")
                    && e.endsWith("|"))
            .collect(Collectors.toList());

        for (String line : lines) {
            for (char c : line.toCharArray()) {
                if (c != 'X'
                    && c != 'O'
                    && c != '|'
                    && c != ' ') {
                    return null;
                }
            }
        }

        FieldState[][] field = new FieldState[3][3];

        int y = 2;
        for (String line : lines) {
            char[] cols = new char[] {
                line.charAt(2),
                line.charAt(4),
                line.charAt(6)
            };

            int x = 0;
            for (char c : cols) {
                FieldState state = FieldState.get(c);
                if (state == null) {
                    return null;
                }
                field[y][x] = state;
                x++;
            }
            y--;
        }

        return new TicTacToeField(field);
    }

    static List<TicTacToeField> parseAll(String output) {
        List<TicTacToeField> fields = new ArrayList<>();

        List<String> lines = output
            .lines()
            .map(String::strip)
            .filter(e -> e.length() > 0)
            .collect(Collectors.toList());

        String candidateField = "";
        boolean insideField = false;
        for (String line : lines) {
            if (line.contains("----") && !insideField) {
                insideField = true;
                candidateField = "";
            } else if (line.contains("----") && insideField) {
                TicTacToeField field = TicTacToeField.parse(candidateField);
                if (field != null) {
                    fields.add(field);
                }
                insideField = false;
            }

            if (insideField && line.startsWith("|")) {
                candidateField += line + "\n";
            }
        }

        return fields;
    }

}


class Clue {
    int x, y;
    Clue(int x, int y) {
        this.x = x;
        this.y = y;
    }
}

public class TicTacToeTest extends MainMethodTest<Clue> {
    public TicTacToeTest() throws Exception {
        super(Main.class);
    }

    static String[] inputs = new String[] {
        "1 1", "1 2", "1 3",
        "2 1", "2 2", "2 3",
        "3 1", "3 2", "3 3"
    };

    String iterateCells(String initial) {
        int index = -1;
        for (int i = 0; i < inputs.length; i++) {
            if (initial.equals(inputs[i])) {
                index = i;
                break;
            }
        }
        if (index == -1) {
            return "";
        }
        String fullInput = "";
        for (int i = index; i < index + 9; i++) {
            fullInput += inputs[i % inputs.length] + "\n";
        }
        return fullInput;
    }

    @Override
    public List<TestCase<Clue>> generateTestCases() {

        List<TestCase<Clue>> tests = new ArrayList<>();

        int i = 0;
        for (String input : inputs) {
            String fullMoveInput = iterateCells(input);

            String[] strNums = input.split(" ");
            int x = Integer.parseInt(strNums[0]);
            int y = Integer.parseInt(strNums[1]);

            if (i % 2 == 1) {
                // mix with incorrect data
                fullMoveInput = "4 " + i + "\n" + fullMoveInput;
            }

            String fullGameInput = "";
            for (int j = 0; j < 9; j++) {
                fullGameInput += fullMoveInput;
            }

            String initial;

            switch (i % 6) {
                case 0: initial = "start user easy\n"; break;
                case 1: initial = "start easy user\n"; break;
                case 2: initial = "start user medium\n"; break;
                case 3: initial = "start medium user\n"; break;
                case 4: initial = "start user hard\n"; break;
                case 5: initial = "start hard user\n"; break;
                default: continue;
            }

            fullGameInput = initial + fullGameInput + "exit";

            tests.add(new TestCase<Clue>()
                .setInput(fullGameInput));

            i++;
        }

        tests.add(new TestCase<Clue>()
            .setInput("start easy easy\nexit"));

        tests.add(new TestCase<Clue>()
            .setInput("start medium medium\nexit"));

        tests.add(new TestCase<Clue>()
            .setInput("start hard hard\nexit"));


        tests.add(new TestCase<Clue>()
            .setInput("start medium easy\nexit"));

        tests.add(new TestCase<Clue>()
            .setInput("start easy medium\nexit"));

        tests.add(new TestCase<Clue>()
            .setInput("start medium hard\nexit"));

        tests.add(new TestCase<Clue>()
            .setInput("start hard medium\nexit"));

        tests.add(new TestCase<Clue>()
            .setInput("start easy hard\nexit"));

        tests.add(new TestCase<Clue>()
            .setInput("start hard easy\nexit"));


        tests.add(new TestCase<Clue>()
            .setInput("start user user\n" +
                "1 1\n" +
                "2 2\n" +
                "1 2\n" +
                "2 1\n" +
                "1 3\n" +
                "exit"));

        return tests;
    }

    @Override
    public CheckResult check(String reply, Clue clue) {

        List<TicTacToeField> fields = TicTacToeField.parseAll(reply);

        if (fields.size() == 0) {
            return new CheckResult(false, "No fields found");
        }

        for (int i = 1; i < fields.size(); i++) {
            TicTacToeField curr = fields.get(i - 1);
            TicTacToeField next = fields.get(i);

            if (!(curr.equalTo(next) || curr.hasNextAs(next))) {
                return new CheckResult(false,
                    "For two fields following each " +
                        "other one is not a continuation " +
                        "of the other.");
            }
        }

        return CheckResult.TRUE;
    }
}

package tictactoe.difficultyLevel;

import tictactoe.Status;

public class Easy extends Player {
    public Easy(char ch) {
        super(ch);
    }

    @Override
    public boolean makeMove(char[][] field, Status status) throws NoMoveAvailable {
        System.out.print(statusMove());
        return randomMove(field, status);
    }

    @Override
    public String statusMove() {
        return "Making move level \"easy\"\n";
    }
}

package tictactoe;

import tictactoe.difficultyLevel.Medium;
import tictactoe.difficultyLevel.Player;
import tictactoe.difficultyLevel.Easy;
import tictactoe.difficultyLevel.User;


class Game {
    private Player playerX;
    private Player playerO;

    static int correctInput(String string) {
        String[] strings = string.split(" ");
        if (strings.length < 1) {
            return -1;
        }
        if (strings[0].equals("exit")) {
            return 0;
        }
        if (strings[0].equals("start")) {
            if (strings.length < 3) {
                return -1;
            } else {
                return Player.isLevel(strings[1]) && Player.isLevel(strings[2]) ? 1 : -1;
            }
        }
        return -1;
    }

    Game(String X, String O) {
        switch (X) {
            case "user": playerX = new User('X'); break;
            case "easy": playerX = new Easy('X'); break;
            case "medium": playerX = new Medium('X'); break;
        }

        switch (O) {
            case "user": playerO = new User('O'); break;
            case "easy": playerO = new Easy('O'); break;
            case "medium": playerO = new Medium('O'); break;
        }
    }

    void processGame() throws Player.NoMoveAvailable {
        Status status = new Status();
        char[][] field = status.toMatrix("         ");
        System.out.println(status.drawField(field));
        while (true) {
            if (playerX.makeMove(field, status)) {
                break;
            }
            if (playerO.makeMove(field, status)) {
                break;
            }
        }
        System.out.println(status.status(field));
    }
}

package tictactoe.difficultyLevel;

import tictactoe.Status;

public class Medium extends Player {
    public Medium(char ch) {
        super(ch);
    }


    /*
    True if move was made.
     */
    private boolean thirdInaRow(char[][] field, char chars) {
        for (int i = 0; i < 3; i++) {
            int numOfLetter = 0;
            int posOfSpace = -1;
            for (int j = 0; j < 3; j++) {
                if (field[i][j] == chars) {
                    numOfLetter++;
                }
                if (field[i][j] == ' ') {
                    posOfSpace = j;
                }
            }
            if (numOfLetter == 2 && posOfSpace != -1) {
                field[i][posOfSpace] = LETTER;
                return true;
            }
        }

        for (int i = 0; i < 3; i++) {
            int numOfLetter = 0;
            int posOfSpace = -1;
            for (int j = 0; j < 3; j++) {
                if (field[j][i] == chars) {
                    numOfLetter++;
                }
                if (field[j][i] == ' ') {
                    posOfSpace = j;
                }
            }
            if (numOfLetter == 2 && posOfSpace != -1) {
                field[posOfSpace][i] = LETTER;
                return true;
            }
        }

        {
            int numOfLetter = 0;
            int posOfSpace = -1;
            for (int i = 0; i < 3; i++) {
                if (field[i][i] == chars) {
                    numOfLetter++;
                }
                if (field[i][i] == ' ') {
                    posOfSpace = i;
                }
            }
            if (numOfLetter == 2 && posOfSpace != -1) {
                field[posOfSpace][posOfSpace] = LETTER;
                return true;
            }
        }

        {
            int numOfLetter = 0;
            int posOfSpace = -1;
            for (int i = 0; i < 3; i++) {
                if (field[i][2-i] == chars) {
                    numOfLetter++;
                }
                if (field[i][2-i] == ' ') {
                    posOfSpace = i;
                }
            }
            if (numOfLetter == 2 && posOfSpace != -1) {
                field[posOfSpace][2-posOfSpace] = LETTER;
                return true;
            }
        }

        return false;
    }

    @Override
    public boolean makeMove(char[][] field, Status status) throws NoMoveAvailable {
        System.out.print(statusMove());
        if (!thirdInaRow(field, LETTER) && !thirdInaRow(field, LETTER == 'X' ? 'O' : 'X')) {
            return randomMove(field, status);
        }
        System.out.println(status.drawField(field));
        String gameStatus = status.status(field);
        return gameStatus.contains("wins") || gameStatus.contains("Draw");
    }

    @Override
    public String statusMove() {
        return "Making move level \"medium\"\n";
    }
}

package tictactoe;

import java.util.HashMap;
import java.util.Map;
import java.util.NoSuchElementException;
import java.util.StringTokenizer;

public class Status {
    public String drawField(char[][] str) {
        StringBuilder stringBuilder = new StringBuilder();
        stringBuilder.append("---------\n");
        for (int i = 0; i < 3; i++) {
            stringBuilder.append("| ");
            for (int j = 0; j < 3; j++) {
                stringBuilder.append(str[i][j]);
                stringBuilder.append(" ");
            }
            stringBuilder.append("|\n");
        }
        stringBuilder.append("---------");
        return stringBuilder.toString();
    }

    public char[][] toMatrix(String str) {
        char[][] ans = new char[3][3];
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                char ch = str.charAt(3*i+j);
                ans[i][j] = ch;
            }
        }
        return ans;
    }

    public String status(char[][] matrix) {
        Map<Character, Integer> num = new HashMap<>();
        /*
        Check if drawField has a lot more X's that O's or vice versa.
         */
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3; j++) {
                int count = num.getOrDefault(matrix[i][j], 0);
                num.put(matrix[i][j], count + 1);
            }
        }
        if (Math.abs(num.getOrDefault('X', 0) - num.getOrDefault('O', 0)) > 1) {
            return "Impossible";
        }

        Map<Character, Boolean> winner = new HashMap<>();
        winner.put('X', false);
        winner.put('O', false);
        /*
        Check who has 3 i a row.
         */
        for (int i = 0; i < 3; i++) {
            char first = matrix[i][0];
            if (matrix[i][1] == first && matrix[i][2] == first) {
                winner.put(first, true);
            }
        }
        for (int j = 0; j < 3; j++) {
            char first = matrix[0][j];
            if (matrix[1][j] == first && matrix[2][j] == first) {
                winner.put(first, true);
            }
        }
        {
            char first = matrix[0][0];
            if (matrix[1][1] == first && matrix[2][2] == first) {
                winner.put(first, true);
            }
        }
        {
            char first = matrix[0][2];
            if (matrix[1][1] == first && matrix[2][0] == first) {
                winner.put(first, true);
            }
        }

        if (winner.get('X') && winner.get('O')) {
            return "Impossible";
        }
        if (winner.get('X')) {
            return "X wins";
        }
        if (winner.get('O')) {
            return "O wins";
        }
        if (num.getOrDefault(' ', 0).equals(0)) {
            return "Draw";
        }
        return "Game not finished";
    }

    public class Answer {
        private Boolean aBoolean;
        private String string;

        Answer(boolean b, String s) {
            aBoolean = b;
            string = s;
        }

        public Boolean getaBoolean() {
            return aBoolean;
        }

        public String getString() {
            return string;
        }
    }

    public Answer parseInput(String input, char[][] field, char ch) {
        StringTokenizer stringTokenizer = new StringTokenizer(input);
        int x;
        int y;
        try {
            x = Integer.parseInt(stringTokenizer.nextToken());
            y = Integer.parseInt(stringTokenizer.nextToken());
        } catch (NumberFormatException | NoSuchElementException e) {
            return new Answer(false, "You should enter numbers!");
        }

        if (x < 1 || x > 3 || y < 1 || y > 3) {
            return new Answer(false, "Coordinates should be from 1 to 3!");
        }

        if (field[3-y][x-1] != ' ') {
            return new Answer(false, "This cell is occupied! Choose another one!");
        }

        field[3-y][x-1] = ch;
        return new Answer(true, drawField(field));
    }
}

package tictactoe.difficultyLevel;

import tictactoe.Status;

import java.util.Scanner;

public class User extends Player {
    public User(char ch) {
        super(ch);
    }

    @Override
    public boolean makeMove(char[][] field, Status status) {
        System.out.print(statusMove());
        Scanner scanner = new Scanner(System.in);
        String input = scanner.nextLine();
        Status.Answer answer = status.parseInput(input, field, LETTER);
        while (!answer.getaBoolean()) {
            System.out.println(answer.getString());
            input = scanner.nextLine();
            answer = status.parseInput(input, field, LETTER);
        }
        System.out.println(answer.getString());
        String gameStatus = status.status(field);
        return gameStatus.contains("wins") || gameStatus.contains("Draw");
    }

    @Override
    public String statusMove() {
        return "Enter the coordinates: ";
    }
}
